import React, {useState, useRef} from 'react';
import { FaStar } from 'react-icons/fa';
import { ratePost } from "../../actions/posts";
import { useDispatch } from "react-redux";


const Rating = ({ post }) => {
  const user = JSON.parse(localStorage.getItem('profile'));
  const [rating, setRating] = useState(null);
  const [hover, setHover] = useState(null);
  const dispatch = useDispatch();
  const [star, setStar] = useState(post?.star);
  const starRef = useRef();

  const handleSubmit = async(e) => {
    e.preventDefault();
    const newStar =  await dispatch(ratePost(`${user?.result?.username}: ${rating}`, post._id));
    
    setStar(newStar);
    starRef.current.scrollIntoView({ behavior: 'smooth' });
  
  };

  const Stars = () => {
    if (rates.length > 0) {
       return star.find()
    }
  }; 

  return (
    <div>
      <form onSubmit={handleSubmit} >
      <label>Ratings</label>
      {[...Array(5)].map((star, i)=>{
         const ratingValue = i + 1;
         return (
          <label>
            <input 
            type="radio" 
            name="rating" 
            value={ratingValue} 
            onClick={() => setRating(ratingValue)} 
        
            />
            <FaStar color={ratingValue <= (hover || rating) ? "#ffc107" : "#e4e5e9"}
            size={20}
            onMouseEnter={() => setHover(ratingValue)}
            onMouseLeave={() => setHover(null)}
   />
          </label>
         )
      })}
      <div ref={starRef} />
      <button type="submit">
        post rating
      </button>
      </form>
    </div>
  )
};

export default Rating;
