import * as React from 'react';
import { useRef } from 'react';
import Rating from '@mui/material/Rating';
import Box from '@mui/material/Box';
import StarIcon from '@mui/icons-material/Star';
import { ratePost } from '../../actions/posts';
import { useDispatch } from 'react-redux';

const labels = {
  0.5: 'Useless',
  1: 'Useless+',
  1.5: 'Poor',
  2: 'Poor+',
  2.5: 'Ok',
  3: 'Ok+',
  3.5: 'Good',
  4: 'Good+',
  4.5: 'Excellent',
  5: 'Excellent+',
};

function getLabelText(rate) {
  return `${rate} Star${rate!== 1 ? 's' : ''}, ${labels[rate]}`;
}

const HoverRating = ({ post }) => {
  const user = JSON.parse(localStorage.getItem('profile'));
  const [rate, setRate] = React.useState(0);
  const [rates, setRates] = React.useState("")
  const [hover, setHover] = React.useState(-1);
  const dispatch = useDispatch();
  const ratesRef = useRef();

  const handleRate = async (e) => {
    e.preventDefault();
    const newRates = await dispatch(ratePost(`${user?.result?.username}: ${rate}`, post._id));

    setRate('');
    setRates(newRates);

    ratesRef.current.scrollIntoView({ behavior: 'smooth' });
  };


  return (
    <Box
      sx={{
        width: 200,
        display: 'flex',
        alignItems: 'center',
      }}
    >
      <Rating
        name="hover-feedback"
        value={rate}
        precision={0.5}
        getLabelText={getLabelText}
        onChange={handleRate}
        
       /*  {(event, newRate) => {
          setValue(newRate);
        }}
 */
        onChangeActive={(event, newHover) => {
          setHover(newHover);
        }}
        emptyIcon={<StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />}
      />
      {rate !== null && (
        <Box sx={{ ml: 2 }}>{labels[hover !== -1 ? hover : rate]}</Box>
      )}
    </Box>
  );
}

export default HoverRating;
