/* 
export default (state, action) => {
      switch(action.type) {
        case 'CLAIM': 
        return{...state}
      }
};
 */

 export default (state = [], action) => {
  switch(action.type) {
    case 'CLAIM':
      return [...claims, action.payload];
     default:
        return claim;
  }
 };
