import { React,useRef, useState } from "react";
import Star from "@mui/icons-material/Star";
import { useDispatch } from "react-redux";
import useStyles from "./styles";

const Js = ({ post })  => {

const user = JSON.parse(localStorage.getItem('profile'));
  
  const [rate, setRate] = useState(0);
  const dispatch = useDispatch();
  const [rates, setRates] = useState(post?.rates);
  const ratesRef = useRef();
  const { classes } = useStyles;
  

  const handleSubmit = async(e) => {
    e.preventDefault();
    const newRates =  await dispatch(commentPost(`${user?.result?.username}: ${rate}`, post._id));

    setRate(0);
    setRates(newRates);

    ratesRef.current.scrollIntoView({ behavior: 'smooth'});
  };

return (
    <div style={{ height: "100vh", width: "100%" }}>
    
        {rates?.map((c) => (
          <>
            <div className={classes.card}>
                <label>Rating</label>
                  <div className="stars">
                    {Array(c.rating).fill(<Star className={classes.star} />)}
                  </div>
          </div>
          </>
        ))}
        {
          <>
           <div>
                <form onSubmit={handleSubmit}>
                  <label>Rating</label>
                  <select onChange={(e) => setRate(e.target.value)}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                  </select>
                <button type="submit" className={classes.submitButton}>
                    post rating
                  </button>
                </form>
              </div>
          </>
        }
    </div>
  );
}

export default Js;
